        }
      }).then(function (response) {
        if (response.data.error == 0) {
          var results = response.data.results;
          if (!results.length) {
            _this.$message({ message: '已经没有纪录了哦', type: 'warning' });
          }
          for (var i = 0; i < results.length; i++) {
            results[i].attendance.attendance = results[i].attendance.attendance.split(",");
            _this.historys.push(results[i]);
          }
        }
        _this.loading = false;
      }, function (response) {
        _this.loading = false;
        _this.$message.error({ showClose: true, message: '日志模块通信失败!' });
      });
    },
    selectARecord: function selectARecord() {
      var _this2 = this;

